strength = -400;

chart = {

    const width = 928;
    const height = 600;

    const types = Array.from(new Set(nodes.map(d =&gt; d.group)));

    const color = d3.scaleOrdinal(types, d3.schemeCategory10);

    const simulation = d3.forceSimulation(nodes)
        .force("link", d3.forceLink(links).id(d =&gt; d.id))
        .force("charge", d3.forceManyBody().strength(strength))
        .force("x", d3.forceX())
        .force("y", d3.forceY());

    const svg = d3.create("svg")
        .attr("viewBox", [-width / 2, -height / 2, width, height])
        .attr("width", width)
        .attr("height", height)
        .attr("style", "max-width: 100%; height: auto; font: 14px sans-serif;");

    const link = svg.append("g")
        .attr("fill", "none")
        .attr("stroke-width", 1.5)
        .selectAll("path")
        .data(links)
        .join("path")
            .attr("stroke", "gray")
            .attr("stroke-width", d =&gt; d.value);

    const node = svg.append("g")
        .selectAll("g")
        .data(nodes)
        .join("g")
            .call(drag(simulation));

    node.append("circle")
        .attr("stroke", "white")
        .attr("stroke-width", 1.5)
        .data(nodes)
        .join("circle")
            .attr("fill", d =&gt; color(d.group))
            .attr("r", d =&gt; d.size);

    node.append("text")
        .attr("x", 8)
        .attr("y", "0.31em")
        .text(d =&gt; d.id)
        .html(d =&gt; d.group === "Co-maintainer" ?
            `&lt;a href="/repometrics-demo/maintainer.html"
                onclick="localStorage.setItem('orgmetricsMaintainer', '${d.id}')"&gt;${d.id}&lt;/a&gt;` :
            ((d.group === "packages" || d.group === "otherPackages") ?
                `&lt;a href="/repometrics-demo/repo.html"
                    onclick="localStorage.setItem('orgmetricsRepo', '${d.id}')"&gt;${d.id}&lt;/a&gt;` :
            d.id))
        .clone(true).lower()
            .attr("fill", "none")
            .attr("stroke", "white")
            .attr("stroke-width", 3);

    simulation.on("tick", () =&gt; {
        link.attr("d", linkArc);
        node.attr("transform", d =&gt; `translate(${d.x},${d.y})`);
    });

    invalidation.then(() =&gt; simulation.stop());

    return Object.assign(svg.node(), {scales: {color}});
}
















function linkArc(d) {
    const r = Math.hypot(d.target.x - d.source.x, d.target.y - d.source.y);
    return `
    M${d.source.x},${d.source.y}
    A${r},${r} 0 0,1 ${d.target.x},${d.target.y}
    `;
}







drag = simulation =&gt; {

    function dragstarted(event, d) {
        if (!event.active) simulation.alphaTarget(0.3).restart();
        d.fx = d.x;
        d.fy = d.y;
    }

    function dragged(event, d) {
        d.fx = event.x;
        d.fy = event.y;
    }

    function dragended(event, d) {
        if (!event.active) simulation.alphaTarget(0);
        d.fx = null;
        d.fy = null;
    }

    return d3.drag()
        .on("start", dragstarted)
        .on("drag", dragged)
        .on("end", dragended);
}
